  "API_specs_data": {
    "api_specification": {
      "openapi": "3.0.0",
      "info": {
        "title": "Customer Churn Prediction API",
        "version": "1.0.0",
        "description": "금융 소비자의 이탈률을 예측하는 API"
      },
      "servers": [
        {
          "url": "https://api.example.com/v1",
          "description": "Production server"
        }
      ],
      "paths": {
        "/churn/predict": {
          "post": {
            "summary": "이탈 예측",
            "description": "사용자의 이탈 가능성을 예측합니다.",
            "tags": [
              "Churn Prediction"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/ChurnPredictionRequest"
                  }
                }
              }
            },
            "responses": {
              "200": {
                "description": "이탈 예측 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/ChurnPredictionResponse"
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/churn/factors": {
          "get": {
            "summary": "주요 이탈 요인 조회",
            "description": "이탈에 영향을 미치는 주요 요인을 조회합니다.",
            "tags": [
              "Churn Factors"
            ],
            "responses": {
              "200": {
                "description": "주요 이탈 요인 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/ChurnFactorsResponse"
                    }
                  }
                }
              },
              "404": {
                "description": "이탈 요인을 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/churn/strategies": {
          "post": {
            "summary": "유지 전략 생성",
            "description": "이탈 가능성이 높은 고객을 위한 맞춤형 유지 전략을 생성합니다.",
            "tags": [
              "Retention Strategies"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/RetentionStrategyRequest"
                  }
                }
              }
            },
            "responses": {
              "201": {
                "description": "유지 전략 생성 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/RetentionStrategyResponse"
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "components": {
        "schemas": {
          "ChurnPredictionRequest": {
            "type": "object",
            "required": [
              "userId",
              "userHistory"
            ],
            "properties": {
              "userId": {
                "type": "string",
                "example": "user_123"
              },
              "userHistory": {
                "type": "object",
                "properties": {
                  "transactionCount": {
                    "type": "integer",
                    "example": 50
                  },
                  "lastTransactionDate": {
                    "type": "string",
                    "format": "date-time",
                    "example": "2024-01-15T10:30:00Z"
                  },
                  "accountAge": {
                    "type": "integer",
                    "example": 24
                  }
                }
              }
            }
          },
          "ChurnPredictionResponse": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "success"
              },
              "data": {
                "type": "object",
                "properties": {
                  "churnProbability": {
                    "type": "number",
                    "format": "float",
                    "example": 0.75
                  },
                  "isAtRisk": {
                    "type": "boolean",
                    "example": true
                  }
                }
              }
            }
          },
          "ChurnFactorsResponse": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "success"
              },
              "data": {
                "type": "array",
                "items": {
                  "type": "string",
                  "example": "Low transaction frequency"
                }
              }
            }
          },
          "RetentionStrategyRequest": {
            "type": "object",
            "required": [
              "userId",
              "riskLevel"
            ],
            "properties": {
              "userId": {
                "type": "string",
                "example": "user_123"
              },
              "riskLevel": {
                "type": "string",
                "enum": [
                  "low",
                  "medium",
                  "high"
                ],
                "example": "high"
              }
            }
          },
          "RetentionStrategyResponse": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "success"
              },
              "data": {
                "type": "object",
                "properties": {
                  "strategy": {
                    "type": "string",
                    "example": "Offer a personalized discount"
                  }
                }
              }
            }
          },
          "Error": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "error"
              },
              "message": {
                "type": "string",
                "example": "오류가 발생했습니다"
              },
              "code": {
                "type": "string",
                "example": "VALIDATION_ERROR"
              }
            }
          }
        }
      }
    }
  }
}

