{
  "project_summary": {
    "project_summary": {
      "title": "보행 약자를 위한 지하철 길찾기 앱",
      "category": "모바일 애플리케이션",
      "target_users": [
        "보행 약자",
        "노약자",
        "장애인"
      ],
      "main_purpose": "보행 약자들이 지하철을 안전하고 편리하게 이용할 수 있도록 지원",
      "key_features": [
        {
          "feature": "계단 없는 경로 안내",
          "description": "엘리베이터, 에스컬레이터 위치와 장애물 없는 출입구를 표시"
        },
        {
          "feature": "실시간 교통 상황",
          "description": "현재 교통 상황과 출입구 혼잡도 정보를 제공"
        },
        {
          "feature": "장애물 정보",
          "description": "장애물 정보를 통해 안전한 경로 추천"
        },
        {
          "feature": "주변 보행 경로 안내",
          "description": "대중교통 외에도 주변의 장애물 없는 보행 경로 제공"
        }
      ],
      "core_technologies": [
        {
          "category": "Frontend",
          "technologies": [
            "React Native",
            "JavaScript"
          ]
        },
        {
          "category": "Backend",
          "technologies": [
            "Node.js",
            "Express"
          ]
        },
        {
          "category": "Database",
          "technologies": [
            "MongoDB"
          ]
        },
        {
          "category": "API",
          "technologies": [
            "Google Maps API",
            "OpenStreetMap"
          ]
        }
      ],
      "problem_solving": {
        "current_problem": "보행 약자들이 지하철 이용 시 겪는 불편함과 안전 문제",
        "solution_approach": "지하철 경로 안내에 보행 약자 친화적인 정보를 추가하여 독립적인 이동 지원",
        "expected_benefits": [
          "보행 약자의 독립성 향상",
          "안전한 이동 경로 제공",
          "편리한 대중교통 이용"
        ]
      },
      "special_features": [
        "실시간 출입구 혼잡도 정보",
        "장애물 정보 제공",
        "주변 장애물 없는 경로 안내"
      ],
      "business_model": {
        "type": "프리미엄 모델",
        "revenue_streams": [
          "광고 수익",
          "프리미엄 기능 구독"
        ],
        "target_market": "보행 약자 및 관련 기관"
      },
      "scalability": {
        "user_capacity": "최대 5만명 동시 접속",
        "expansion_plan": "다른 도시 및 대중교통 시스템으로 서비스 확장",
        "integration_capability": "타 대중교통 API와의 연동"
      },
      "development_timeline": {
        "estimated_duration": "4개월",
        "key_milestones": [
          {
            "phase": "기획 및 설계",
            "duration": "1개월"
          },
          {
            "phase": "MVP 개발",
            "duration": "2개월"
          },
          {
            "phase": "테스트 및 배포",
            "duration": "1개월"
          }
        ]
      },
      "success_metrics": [
        "사용자 증가율",
        "앱 사용 빈도",
        "사용자 만족도",
        "안전 사고 발생률"
      ],
      "challenges_and_risks": [
        {
          "challenge": "정확한 데이터 수집",
          "mitigation": "지속적인 데이터 업데이트 및 사용자 피드백 반영"
        },
        {
          "challenge": "사용자 접근성",
          "mitigation": "다양한 사용자 테스트를 통한 UI/UX 개선"
        }
      ]
    }
  },
  "relationships_data": [
    {
      "from": "User",
      "to": "Route",
      "type": "one-to-many",
      "foreign_key": "user_id",
      "constraint_name": "fk_route_user"
    },
    {
      "from": "Route",
      "to": "TrafficInfo",
      "type": "one-to-one",
      "foreign_key": "route_id",
      "constraint_name": "fk_traffic_route"
    },
    {
      "from": "Route",
      "to": "Obstacle",
      "type": "one-to-many",
      "foreign_key": "route_id",
      "constraint_name": "fk_obstacle_route"
    }
  ],
  "ERD_data": [
    {
      "name": "User",
      "attributes": [
        {
          "name": "user_id",
          "data_type": "INTEGER",
          "is_primary_key": true,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "username",
          "data_type": "VARCHAR(50)",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "email",
          "data_type": "VARCHAR(100)",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": true
        }
      ]
    },
    {
      "name": "Route",
      "attributes": [
        {
          "name": "route_id",
          "data_type": "INTEGER",
          "is_primary_key": true,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "start_location",
          "data_type": "VARCHAR(100)",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "end_location",
          "data_type": "VARCHAR(100)",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "has_elevator",
          "data_type": "BOOLEAN",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "has_escalator",
          "data_type": "BOOLEAN",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "obstacle_free_entry",
          "data_type": "BOOLEAN",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        }
      ]
    },
    {
      "name": "TrafficInfo",
      "attributes": [
        {
          "name": "traffic_id",
          "data_type": "INTEGER",
          "is_primary_key": true,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "route_id",
          "data_type": "INTEGER",
          "is_primary_key": false,
          "is_foreign_key": true,
          "is_nullable": false
        },
        {
          "name": "real_time_status",
          "data_type": "VARCHAR(100)",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "congestion_level",
          "data_type": "VARCHAR(50)",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        }
      ]
    },
    {
      "name": "Obstacle",
      "attributes": [
        {
          "name": "obstacle_id",
          "data_type": "INTEGER",
          "is_primary_key": true,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "location",
          "data_type": "VARCHAR(100)",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": false
        },
        {
          "name": "description",
          "data_type": "VARCHAR(255)",
          "is_primary_key": false,
          "is_foreign_key": false,
          "is_nullable": true
        }
      ]
    }
  ],
  "API_specs_data": {
    "api_specification": {
      "openapi": "3.0.0",
      "info": {
        "title": "Accessible Subway Navigation API",
        "version": "1.0.0",
        "description": "보행 약자를 위한 지하철 길찾기 API"
      },
      "servers": [
        {
          "url": "https://api.accessiblesubway.com/v1",
          "description": "Production server"
        }
      ],
      "paths": {
        "/routes": {
          "get": {
            "summary": "경로 조회",
            "description": "보행 약자를 위한 계단 없는 경로를 조회합니다.",
            "tags": [
              "Routes"
            ],
            "parameters": [
              {
                "name": "start",
                "in": "query",
                "description": "출발 지점의 좌표 (위도, 경도)",
                "required": true,
                "schema": {
                  "type": "string",
                  "example": "37.5665,126.978"
                }
              },
              {
                "name": "end",
                "in": "query",
                "description": "도착 지점의 좌표 (위도, 경도)",
                "required": true,
                "schema": {
                  "type": "string",
                  "example": "37.5700,126.985"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "경로 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "object",
                          "properties": {
                            "route": {
                              "type": "array",
                              "items": {
                                "$ref": "#/components/schemas/RouteStep"
                              }
                            },
                            "elevators": {
                              "type": "array",
                              "items": {
                                "$ref": "#/components/schemas/Facility"
                              }
                            },
                            "escalators": {
                              "type": "array",
                              "items": {
                                "$ref": "#/components/schemas/Facility"
                              }
                            },
                            "obstacles": {
                              "type": "array",
                              "items": {
                                "$ref": "#/components/schemas/Obstacle"
                              }
                            }
                          }
                        }
                      }
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/facilities": {
          "get": {
            "summary": "시설 정보 조회",
            "description": "지하철역 내 엘리베이터 및 에스컬레이터 위치를 조회합니다.",
            "tags": [
              "Facilities"
            ],
            "parameters": [
              {
                "name": "stationId",
                "in": "query",
                "description": "지하철역 ID",
                "required": true,
                "schema": {
                  "type": "string",
                  "example": "123"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "시설 정보 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/Facility"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "지하철역을 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/traffic": {
          "get": {
            "summary": "실시간 교통 상황 조회",
            "description": "현재 교통 상황을 조회합니다.",
            "tags": [
              "Traffic"
            ],
            "responses": {
              "200": {
                "description": "교통 상황 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "object",
                          "properties": {
                            "congestionLevel": {
                              "type": "string",
                              "example": "low"
                            },
                            "message": {
                              "type": "string",
                              "example": "현재 교통 상황은 원활합니다."
                            }
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "/entrances": {
          "get": {
            "summary": "장애물 없는 출입구 조회",
            "description": "장애물 없는 출입구 정보를 조회합니다.",
            "tags": [
              "Entrances"
            ],
            "parameters": [
              {
                "name": "stationId",
                "in": "query",
                "description": "지하철역 ID",
                "required": true,
                "schema": {
                  "type": "string",
                  "example": "123"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "출입구 정보 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/Entrance"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "지하철역을 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "components": {
        "schemas": {
          "RouteStep": {
            "type": "object",
            "properties": {
              "instruction": {
                "type": "string",
                "example": "지하철 1호선 탑승"
              },
              "duration": {
                "type": "integer",
                "example": 15
              },
              "distance": {
                "type": "number",
                "example": 1.2
              }
            }
          },
          "Facility": {
            "type": "object",
            "properties": {
              "type": {
                "type": "string",
                "example": "elevator"
              },
              "location": {
                "type": "string",
                "example": "1번 출입구"
              }
            }
          },
          "Obstacle": {
            "type": "object",
            "properties": {
              "description": {
                "type": "string",
                "example": "장애물: 공사 중"
              },
              "location": {
                "type": "string",
                "example": "2번 출입구"
              }
            }
          },
          "Entrance": {
            "type": "object",
            "properties": {
              "location": {
                "type": "string",
                "example": "3번 출입구"
              },
              "accessible": {
                "type": "boolean",
                "example": true
              }
            }
          },
          "Error": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "error"
              },
              "message": {
                "type": "string",
                "example": "오류가 발생했습니다"
              },
              "code": {
                "type": "string",
                "example": "VALIDATION_ERROR"
              }
            }
          }
        }
      }
    }
  }
}

{
  "project_summary": {
