              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/users/{userId}/schedule": {
          "get": {
            "summary": "최적의 공부 시간표 조회",
            "description": "사용자의 집중도 패턴에 기반하여 최적의 공부 시간표를 조회합니다.",
            "tags": [
              "Users"
            ],
            "parameters": [
              {
                "name": "userId",
                "in": "path",
                "required": true,
                "description": "사용자 ID",
                "schema": {
                  "type": "integer"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "시간표 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Schedule"
                    }
                  }
                }
              },
              "404": {
                "description": "사용자를 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          },
          "put": {
            "summary": "시간표 재조정",
            "description": "학습 후 피드백 결과에 따라 시간표를 재조정합니다.",
