          "post": {
            "summary": "최적의 시간표 생성",
            "description": "학생의 선호 과목과 가능한 시간대를 기반으로 최적의 시간표를 생성합니다.",
            "tags": [
              "Timetable"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/TimetableRequest"
                  }
                }
              }
            },
            "responses": {
              "200": {
                "description": "시간표 생성 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/TimetableResponse"
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/courses/{courseId}/feedback": {
          "post": {
            "summary": "과목 피드백 제출",
            "description": "학생이 과목에 대한 만족도를 제출합니다.",
            "tags": [
              "Feedback"
            ],
            "parameters": [
              {
                "name": "courseId",
                "in": "path",
                "required": true,
                "description": "과목 ID",
                "schema": {
                  "type": "integer"
                }
              }
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/FeedbackRequest"
                  }
                }
              }
            },
            "responses": {
              "201": {
                "description": "피드백 제출 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/FeedbackResponse"
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
