            "properties": {
              "preferredCourses": {
                "type": "array",
                "items": {
                  "type": "integer"
                },
                "example": [
                  101,
                  102,
                  103
                ]
              },
              "availableTimes": {
                "type": "array",
                "items": {
                  "type": "string",
                  "format": "date-time"
                },
                "example": [
                  "2024-03-01T09:00:00Z",
                  "2024-03-01T10:00:00Z"
                ]
              }
            }
          },
          "TimetableResponse": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "success"
              },
              "timetable": {
                "type": "array",
                "items": {
                  "$ref": "#/components/schemas/Class"
                }
              }
            }
          },
          "Class": {
            "type": "object",
            "properties": {
              "courseId": {
                "type": "integer",
                "example": 101
              },
              "courseName": {
                "type": "string",
                "example": "Introduction to Programming"
              },
              "professor": {
                "type": "string",
                "example": "Dr. Smith"
              },
              "time": {
                "type": "string",
                "format": "date-time",
                "example": "2024-03-01T09:00:00Z"
              },
              "location": {
                "type": "string",
                "example": "Room 101"
              }
            }
          },
          "FeedbackRequest": {
            "type": "object",
            "required": [
              "satisfaction",
              "comments"
            ],
            "properties": {
              "satisfaction": {
                "type": "integer",
                "minimum": 1,
                "maximum": 5,
                "example": 4
              },
              "comments": {
                "type": "string",
                "example": "교수님이 수업을 잘 진행하셨습니다."
              }
            }
          },
          "FeedbackResponse": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "success"
              },
              "message": {
                "type": "string",
                "example": "피드백이 성공적으로 제출되었습니다."
              }
            }
          },
          "Course": {
            "type": "object",
            "properties": {
              "id": {
                "type": "integer",
                "example": 101
              },
              "name": {
                "type": "string",
                "example": "Introduction to Programming"
              },
              "difficulty": {
                "type": "string",
                "example": "중간"
              },
              "professorStyle": {
                "type": "string",
                "example": "강의 중심"
              },
              "teamProject": {
                "type": "boolean",
                "example": true
              }
            }
          },
          "Error": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "error"
              },
              "message": {
                "type": "string",
                "example": "오류가 발생했습니다."
              },
              "code": {
                "type": "string",
                "example": "VALIDATION_ERROR"
              }
            }
          }
        }
      }
    }
