      "paths": {
        "/transcriptions": {
          "post": {
            "summary": "음성 인식 및 자막 생성",
            "description": "사용자의 음성을 실시간으로 텍스트로 변환하고 시각화합니다.",
            "tags": [
              "Transcriptions"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "properties": {
                      "audioStream": {
                        "type": "string",
                        "format": "binary",
                        "description": "실시간 음성 데이터 스트림"
                      },
                      "sessionId": {
                        "type": "string",
                        "description": "세션 ID"
                      }
                    },
                    "required": [
                      "audioStream",
                      "sessionId"
                    ]
                  }
                }
              },
              "responses": {
                "200": {
                  "description": "음성 인식 성공",
                  "content": {
                    "application/json": {
                      "schema": {
                        "type": "object",
                        "properties": {
                          "status": {
                            "type": "string",
                            "example": "success"
                          },
                          "transcription": {
                            "type": "string",
                            "example": "안녕하세요, 오늘 회의에 대해 이야기해봅시다."
                          },
                          "visualization": {
                            "type": "object",
                            "properties": {
                              "emotion": {
                                "type": "string",
                                "example": "happy"
                              },
                              "tone": {
                                "type": "string",
                                "example": "neutral"
                              }
                            }
                          }
                        }
                      }
                    }
                  }
                },
                "400": {
                  "description": "잘못된 요청",
                  "content": {
                    "application/json": {
                      "schema": {
                        "$ref": "#/components/schemas/Error"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "/transcriptions/{sessionId}": {
          "get": {
            "summary": "세션 기록 조회",
            "description": "저장된 세션의 음성 인식 기록을 조회합니다.",
            "tags": [
              "Transcriptions"
            ],
            "parameters": [
              {
                "name": "sessionId",
                "in": "path",
                "required": true,
                "description": "세션 ID",
                "schema": {
                  "type": "string"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "세션 기록 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "transcription": {
                          "type": "array",
                          "items": {
                            "type": "object",
                            "properties": {
                              "text": {
                                "type": "string",
                                "example": "안녕하세요, 오늘 회의에 대해 이야기해봅시다."
                              },
                              "timestamp": {
                                "type": "string",
                                "format": "date-time",
                                "example": "2024-01-15T10:30:00Z"
