                                "type": "object",
                                "properties": {
                                  "emotion": {
                                    "type": "string",
                                    "example": "happy"
                                  },
                                  "tone": {
                                    "type": "string",
                                    "example": "neutral"
                                  }
                                }
                              }
                            }
                          }
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "세션을 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
