  "API_specs_data": {
    "api_specification": {
      "openapi": "3.0.0",
      "info": {
        "title": "Smart Attendance and Learning Space Management API",
        "version": "1.0.0",
        "description": "대학 내 강의 출결과 자율 학습 공간 예약을 효율적으로 관리하기 위한 API"
      },
      "servers": [
        {
          "url": "https://api.example.com/v1",
          "description": "Production server"
        }
      ],
      "paths": {
        "/attendance": {
          "post": {
            "summary": "출결 인증",
            "description": "QR코드 스캔 또는 BLE를 통해 출결을 인증합니다.",
            "tags": [
              "Attendance"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "properties": {
                      "studentId": {
                        "type": "string",
                        "example": "S123456"
                      },
                      "classId": {
                        "type": "string",
                        "example": "CSE101"
                      },
                      "method": {
                        "type": "string",
                        "enum": [
                          "QR",
                          "BLE"
                        ],
                        "example": "QR"
                      }
                    },
                    "required": [
                      "studentId",
                      "classId",
                      "method"
                    ]
                  }
                }
              },
              "responses": {
                "200": {
                  "description": "출결 인증 성공",
                  "content": {
                    "application/json": {
                      "schema": {
                        "type": "object",
                        "properties": {
                          "status": {
                            "type": "string",
                            "example": "success"
                          },
                          "message": {
                            "type": "string",
                            "example": "출결 인증이 완료되었습니다."
                          }
                        }
                      }
                    }
                  }
                },
                "400": {
                  "description": "잘못된 요청",
                  "content": {
                    "application/json": {
                      "schema": {
                        "$ref": "#/components/schemas/Error"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "/spaces": {
          "get": {
            "summary": "공용 공간 목록 조회",
            "description": "예약 가능한 공용 공간 목록을 조회합니다.",
            "tags": [
              "Spaces"
            ],
            "parameters": [
              {
                "name": "type",
                "in": "query",
                "description": "공간 유형 (스터디룸, 실습실 등)",
                "required": false,
                "schema": {
                  "type": "string",
                  "example": "스터디룸"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "공용 공간 목록 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/Space"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "공간을 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          },
          "post": {
            "summary": "공용 공간 예약",
            "description": "공용 공간을 예약합니다.",
            "tags": [
              "Spaces"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/ReserveSpaceRequest"
                  }
                }
              },
              "responses": {
                "201": {
                  "description": "공용 공간 예약 성공",
                  "content": {
                    "application/json": {
                      "schema": {
                        "type": "object",
                        "properties": {
                          "status": {
                            "type": "string",
                            "example": "success"
                          },
                          "data": {
                            "$ref": "#/components/schemas/Space"
                          }
                        }
                      }
                    }
                  }
                },
                "400": {
                  "description": "잘못된 요청 데이터",
                  "content": {
                    "application/json": {
                      "schema": {
                        "$ref": "#/components/schemas/Error"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "/attendance/stats": {
          "get": {
            "summary": "출석률 분석",
            "description": "출석률 및 예약 공간 이용률 데이터를 제공합니다.",
            "tags": [
              "Statistics"
            ],
            "responses": {
              "200": {
                "description": "출석률 데이터 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "object",
                          "properties": {
                            "attendanceRate": {
                              "type": "number",
                              "example": 95.5
                            },
                            "spaceUtilizationRate": {
                              "type": "number",
                              "example": 80.0
                            }
                          }
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "데이터를 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "components": {
        "schemas": {
          "Space": {
            "type": "object",
            "properties": {
              "id": {
                "type": "string",
                "example": "space_001"
              },
              "name": {
                "type": "string",
                "example": "스터디룸 A"
              },
              "type": {
                "type": "string",
                "example": "스터디룸"
              },
              "capacity": {
                "type": "integer",
                "example": 10
              },
              "availability": {
                "type": "boolean",
                "example": true
              }
            }
          },
          "ReserveSpaceRequest": {
            "type": "object",
            "properties": {
              "studentId": {
                "type": "string",
                "example": "S123456"
              },
              "spaceId": {
                "type": "string",
                "example": "space_001"
              },
              "startTime": {
                "type": "string",
                "format": "date-time",
                "example": "2024-01-15T10:00:00Z"
              },
              "endTime": {
                "type": "string",
                "format": "date-time",
                "example": "2024-01-15T12:00:00Z"
              }
            },
            "required": [
              "studentId",
              "spaceId",
              "startTime",
              "endTime"
            ]
          },
          "Error": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "error"
              },
              "message": {
                "type": "string",
                "example": "오류가 발생했습니다"
              },
              "code": {
                "type": "string",
                "example": "VALIDATION_ERROR"
              }
            }
          }
        }
      }
    }
  }
