      "paths": {
        "/environment": {
          "get": {
            "summary": "환경 상태 조회",
            "description": "온실 내부의 현재 환경 상태(온도, 습도, CO2 농도, 토양 수분)를 조회합니다.",
            "tags": [
              "Environment"
            ],
            "responses": {
              "200": {
                "description": "환경 상태 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "object",
                          "properties": {
                            "temperature": {
                              "type": "number",
                              "example": 22.5
                            },
                            "humidity": {
                              "type": "number",
                              "example": 60
                            },
                            "co2": {
                              "type": "number",
                              "example": 400
                            },
                            "soilMoisture": {
                              "type": "number",
                              "example": 30
                            }
                          }
                        }
                      }
                    }
                  }
                }
              },
              "500": {
                "description": "서버 오류",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/control": {
          "post": {
            "summary": "환경 제어",
            "description": "환기, 급수, 조명 등을 자동으로 조절합니다.",
            "tags": [
              "Control"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "properties": {
                      "ventilation": {
                        "type": "boolean",
                        "example": true
                      },
                      "watering": {
                        "type": "boolean",
                        "example": false
                      },
                      "lighting": {
                        "type": "boolean",
                        "example": true
                      }
                    }
                  }
                }
              },
              "responses": {
                "200": {
                  "description": "환경 제어 성공",
                  "content": {
                    "application/json": {
                      "schema": {
                        "type": "object",
                        "properties": {
                          "status": {
                            "type": "string",
                            "example": "success"
                          },
                          "message": {
                            "type": "string",
                            "example": "환경이 성공적으로 조절되었습니다."
                          }
                        }
                      }
                    }
                  }
                },
                "400": {
                  "description": "잘못된 요청",
                  "content": {
                    "application/json": {
                      "schema": {
                        "$ref": "#/components/schemas/Error"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "/crop-records": {
          "get": {
            "summary": "생육 기록 조회",
            "description": "작물의 생육 기록을 조회합니다.",
            "tags": [
              "Crop Records"
            ],
            "parameters": [
              {
                "name": "cropId",
                "in": "query",
                "required": true,
                "description": "작물 ID",
