    "api_specification": {
      "openapi": "3.0.0",
      "info": {
        "title": "Festival Crowd Management API",
        "version": "1.0.0",
        "description": "관람객의 이동 경로와 체류 데이터를 실시간 분석하는 API"
      },
      "servers": [
        {
          "url": "https://api.festivalmanagement.com/v1",
          "description": "Production server"
        }
      ],
      "paths": {
        "/crowd-data": {
          "get": {
            "summary": "실시간 혼잡도 데이터 조회",
            "description": "현재 혼잡도 데이터를 조회합니다.",
            "tags": [
              "Crowd Management"
            ],
            "responses": {
              "200": {
                "description": "혼잡도 데이터 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/CrowdData"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "500": {
                "description": "서버 오류",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/recommended-paths": {
          "get": {
            "summary": "추천 동선 조회",
            "description": "관람객에게 추천하는 동선을 조회합니다.",
            "tags": [
              "Navigation"
            ],
            "parameters": [
              {
                "name": "userId",
                "in": "query",
                "required": true,
                "description": "사용자 ID",
                "schema": {
                  "type": "string"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "추천 동선 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/RecommendedPath"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "사용자를 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/alerts": {
          "get": {
            "summary": "이벤트 알림 조회",
            "description": "관람객에게 전송된 이벤트 알림을 조회합니다.",
            "tags": [
              "Notifications"
            ],
            "parameters": [
              {
                "name": "userId",
                "in": "query",
                "required": true,
                "description": "사용자 ID",
                "schema": {
                  "type": "string"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "이벤트 알림 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "array",
