                "type": "object",
                "additionalProperties": true
              }
            }
          },
          "Report": {
            "type": "object",
            "properties": {
              "reportId": {
                "type": "string",
                "example": "report_2024_01"
              },
              "generatedAt": {
                "type": "string",
                "format": "date-time",
                "example": "2024-01-15T12:00:00Z"
              },
              "summary": {
                "type": "string",
                "example": "행사 동안의 혼잡도 및 관람객 흐름 분석"
              }
            }
          },
          "Error": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "error"
              },
              "message": {
                "type": "string",
                "example": "오류가 발생했습니다"
