  "API_specs_data": {
    "api_specification": {
      "openapi": "3.0.0",
      "info": {
        "title": "Traffic Safety System API",
        "version": "1.0.0",
        "description": "운전 중 실시간으로 사고 위험을 분석하고 알림을 제공하는 교통안전 시스템"
      },
      "servers": [
        {
          "url": "https://api.traffic-safety.com/v1",
          "description": "Production server"
        }
      ],
      "paths": {
        "/alerts": {
          "get": {
            "summary": "실시간 사고 위험 알림 조회",
            "description": "운전 중 발생한 사고 위험 알림을 조회합니다.",
            "tags": [
              "Alerts"
            ],
            "parameters": [
              {
                "name": "vehicleId",
                "in": "query",
                "description": "차량 ID",
                "required": true,
                "schema": {
                  "type": "string"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "알림 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/Alert"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "차량을 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/risk-analysis": {
          "post": {
            "summary": "사고 위험 분석 요청",
            "description": "차량의 센서 및 데이터로 사고 위험을 분석합니다.",
            "tags": [
              "Risk Analysis"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/RiskAnalysisRequest"
                  }
                }
              }
            },
            "responses": {
              "200": {
                "description": "위험 분석 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/RiskAnalysisResponse"
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/accident-prone-areas": {
          "get": {
            "summary": "사고 다발 지역 정보 조회",
            "description": "사고 다발 지역 정보를 조회합니다.",
            "tags": [
              "Accident Prone Areas"
            ],
            "responses": {
              "200": {
                "description": "사고 다발 지역 정보 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/AccidentProneArea"
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      },
      "components": {
        "schemas": {
          "Alert": {
            "type": "object",
            "properties": {
              "alertId": {
                "type": "string",
                "example": "alert_12345"
              },
              "message": {
                "type": "string",
                "example": "급정거 감지: 사고 위험!"
              },
              "timestamp": {
                "type": "string",
                "format": "date-time",
                "example": "2024-01-15T10:30:00Z"
              }
            }
          },
          "RiskAnalysisRequest": {
            "type": "object",
            "required": [
              "vehicleId",
              "sensorData"
            ],
            "properties": {
              "vehicleId": {
                "type": "string",
                "example": "vehicle_123"
              },
              "sensorData": {
                "type": "object",
                "properties": {
                  "speed": {
                    "type": "number",
                    "example": 80
                  },
                  "distanceToFrontCar": {
                    "type": "number",
                    "example": 15
                  },
                  "weatherCondition": {
                    "type": "string",
                    "example": "rain"
                  },
                  "brakingIntensity": {
                    "type": "number",
                    "example": 0.8
                  }
                }
              }
            }
          },
          "RiskAnalysisResponse": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "success"
              },
              "riskLevel": {
                "type": "string",
                "example": "high"
              },
              "recommendations": {
                "type": "array",
                "items": {
                  "type": "string",
                  "example": "속도를 줄이세요."
                }
              }
            }
          },
          "AccidentProneArea": {
            "type": "object",
            "properties": {
              "areaId": {
                "type": "string",
                "example": "area_001"
              },
              "location": {
                "type": "string",
                "example": "서울특별시 강남구"
              },
              "accidentCount": {
                "type": "integer",
                "example": 15
              }
            }
          },
          "Error": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "error"
              },
              "message": {
                "type": "string",
                "example": "오류가 발생했습니다"
              },
              "code": {
                "type": "string",
                "example": "VALIDATION_ERROR"
              }
            }
          }
        },
        "securitySchemes": {
          "bearerAuth": {
            "type": "http",
            "scheme": "bearer",
            "bearerFormat": "JWT"
          }
        }
      },
      "security": [
        {
          "bearerAuth": []
        }
      ]
    }
  }
}

