                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/transactions/{transactionId}/analyze": {
          "post": {
            "summary": "거래 분석",
            "description": "특정 거래에 대한 이상 거래 탐지 및 위험 분석을 수행합니다",
