                "example": "Account A"
              },
              "accountTo": {
                "type": "string",
                "example": "Account B"
              },
              "amount": {
                "type": "number",
                "format": "float",
                "example": 1000.0
              },
              "timestamp": {
                "type": "string",
                "format": "date-time",
                "example": "2024-01-15T10:30:00Z"
              }
