              "id": {
                "type": "integer",
                "example": 1
              },
              "transactionId": {
                "type": "integer",
                "example": 1
              },
              "message": {
                "type": "string",
                "example": "이 거래는 위험으로 분류되었습니다."
              },
              "createdAt": {
                "type": "string",
                "format": "date-time",
                "example": "2024-01-15T10:30:00Z"
              }
            }
          },
          "Pagination": {
            "type": "object",
            "properties": {
              "currentPage": {
                "type": "integer",
                "example": 1
              },
              "totalPages": {
                "type": "integer",
                "example": 10
              },
              "totalItems": {
                "type": "integer",
                "example": 100
              },
              "itemsPerPage": {
                "type": "integer",
                "example": 10
              }
