      "paths": {
        "/users": {
          "post": {
            "summary": "새 사용자 등록",
            "description": "새로운 사용자를 등록합니다",
            "tags": [
              "Users"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/CreateUserRequest"
                  }
                }
              }
            },
            "responses": {
              "201": {
                "description": "사용자 등록 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/User"
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/users/{userId}": {
          "get": {
            "summary": "특정 사용자 조회",
            "description": "사용자 ID로 특정 사용자 정보를 조회합니다",
            "tags": [
              "Users"
            ],
            "parameters": [
              {
                "name": "userId",
                "in": "path",
                "required": true,
                "description": "사용자 ID",
                "schema": {
                  "type": "integer"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "사용자 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/User"
                    }
                  }
                }
              },
              "404": {
                "description": "사용자를 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          },
          "put": {
            "summary": "사용자 정보 수정",
            "description": "사용자 정보를 수정합니다",
            "tags": [
              "Users"
            ],
            "parameters": [
              {
                "name": "userId",
                "in": "path",
                "required": true,
                "description": "사용자 ID",
                "schema": {
                  "type": "integer"
                }
              }
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/UpdateUserRequest"
                  }
                }
              }
            },
            "responses": {
              "200": {
                "description": "사용자 정보 수정 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/User"
                    }
                  }
                }
              },
              "404": {
                "description": "사용자를 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          },
          "delete": {
            "summary": "사용자 삭제",
            "description": "사용자를 삭제합니다",
            "tags": [
              "Users"
            ],
            "parameters": [
              {
                "name": "userId",
                "in": "path",
                "required": true,
                "description": "사용자 ID",
                "schema": {
                  "type": "integer"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "사용자 삭제 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "message": {
                          "type": "string",
                          "example": "사용자가 성공적으로 삭제되었습니다"
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "사용자를 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
