                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/CreateReminderRequest"
                  }
                }
              }
            },
            "responses": {
              "201": {
                "description": "리마인더 설정 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Reminder"
                    }
