              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/focus-tools": {
          "post": {
            "summary": "집중력 도구 사용",
            "description": "포모도로 타이머와 스마트폰 차단 기능을 사용합니다",
