              }
            },
            "responses": {
              "200": {
                "description": "집중력 도구 사용 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "message": {
                          "type": "string",
                          "example": "집중력 도구가 활성화되었습니다"
                        }
                      }
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
