              "Contracts"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "properties": {
                      "contractText": {
                        "type": "string",
                        "description": "분석할 계약서의 텍스트"
                      }
                    },
                    "required": [
                      "contractText"
                    ]
                  }
                }
              }
            },
            "responses": {
              "200": {
                "description": "계약서 분석 성공",
                "content": {
