                              "type": "array",
                              "items": {
                                "type": "string"
                              }
                            },
                            "explanations": {
                              "type": "array",
                              "items": {
                                "type": "string"
                              }
                            }
                          }
                        }
                      }
                    }
                  }
                }
              },
