      "info": {
        "title": "Anomaly Transaction Detection API",
        "version": "1.0.0",
        "description": "카드 거래 데이터를 기반으로 이상 거래를 자동 탐지하는 금융 보안 시스템 API"
      },
      "servers": [
        {
          "url": "https://api.financialsecurity.com/v1",
          "description": "Production server"
        }
      ],
      "paths": {
        "/transactions": {
          "post": {
            "summary": "거래 데이터 수집",
            "description": "새로운 거래 데이터를 수집하고 전처리합니다",
            "tags": [
              "Transactions"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "$ref": "#/components/schemas/TransactionData"
                  }
                }
              }
            },
            "responses": {
              "201": {
                "description": "거래 데이터 수집 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "$ref": "#/components/schemas/Transaction"
                        }
                      }
                    }
                  }
                }
              },
              "400": {
                "description": "잘못된 요청 데이터",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/transactions/anomalies": {
          "get": {
            "summary": "이상 거래 탐지",
            "description": "모든 거래 데이터에서 이상 거래를 탐지합니다",
            "tags": [
              "Anomalies"
            ],
            "responses": {
              "200": {
                "description": "이상 거래 탐지 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "anomalies": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/Anomaly"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "500": {
                "description": "서버 오류",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        },
        "/users/login": {
          "post": {
            "summary": "사용자 로그인",
            "description": "웹 인터페이스를 통한 사용자 로그인 기능",
