  "API_specs_data": {
    "api_specification": {
      "openapi": "3.0.0",
      "info": {
        "title": "Business Location Recommendation API",
        "version": "1.0.0",
        "description": "소상공인을 위한 최적의 상점 입지 추천 API"
      },
      "servers": [
        {
          "url": "https://api.example.com/v1",
          "description": "Production server"
        }
      ],
      "paths": {
        "/locations/recommend": {
          "post": {
            "summary": "상점 입지 추천",
            "description": "예비 창업자가 입력한 업종과 자본 규모에 따라 최적의 상점 입지를 추천합니다.",
            "tags": [
              "Locations"
            ],
            "requestBody": {
              "required": true,
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "required": [
                      "businessType",
                      "capital"
                    ],
                    "properties": {
                      "businessType": {
                        "type": "string",
                        "example": "카페"
                      },
                      "capital": {
                        "type": "number",
                        "example": 50000000
                      }
                    }
                  }
                }
              },
              "responses": {
                "200": {
                  "description": "추천 성공",
                  "content": {
                    "application/json": {
                      "schema": {
                        "type": "object",
                        "properties": {
                          "status": {
                            "type": "string",
                            "example": "success"
                          },
                          "data": {
                            "type": "array",
                            "items": {
                              "$ref": "#/components/schemas/RecommendedLocation"
                            }
                          }
                        }
                      }
                    }
                  }
                },
                "400": {
                  "description": "잘못된 요청",
                  "content": {
                    "application/json": {
                      "schema": {
                        "$ref": "#/components/schemas/Error"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "/locations/{locationId}/report": {
          "get": {
            "summary": "상점 입지 리포트 조회",
            "description": "추천된 상점 입지에 대한 상세 리포트를 조회합니다.",
            "tags": [
              "Locations"
            ],
            "parameters": [
              {
                "name": "locationId",
                "in": "path",
                "required": true,
                "description": "추천된 상점 입지 ID",
                "schema": {
                  "type": "string"
                }
              }
            ],
            "responses": {
              "200": {
                "description": "리포트 조회 성공",
                "content": {
                  "application/json": {
                    "schema": {
                      "type": "object",
                      "properties": {
                        "status": {
                          "type": "string",
                          "example": "success"
                        },
                        "data": {
                          "$ref": "#/components/schemas/LocationReport"
                        }
                      }
                    }
                  }
                }
              },
              "404": {
                "description": "상점 입지를 찾을 수 없음",
                "content": {
                  "application/json": {
                    "schema": {
                      "$ref": "#/components/schemas/Error"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "components": {
        "schemas": {
          "RecommendedLocation": {
            "type": "object",
            "properties": {
              "locationId": {
                "type": "string",
                "example": "location_123"
              },
              "address": {
                "type": "string",
                "example": "서울특별시 강남구 테헤란로 123"
              },
              "expectedRevenue": {
                "type": "number",
                "example": 30000000
              },
              "competitionRate": {
                "type": "number",
                "example": 0.2
              },
              "footTraffic": {
                "type": "number",
                "example": 1500
              }
            }
          },
          "LocationReport": {
            "type": "object",
            "properties": {
              "locationId": {
                "type": "string",
                "example": "location_123"
              },
              "address": {
                "type": "string",
                "example": "서울특별시 강남구 테헤란로 123"
              },
              "expectedRevenue": {
                "type": "number",
                "example": 30000000
              },
              "competitionRate": {
                "type": "number",
                "example": 0.2
              },
              "footTraffic": {
                "type": "number",
                "example": 1500
              },
              "growthRate": {
                "type": "number",
                "example": 0.05
              },
              "reportVisualization": {
                "type": "string",
                "example": "https://example.com/report/location_123"
              }
            }
          },
          "Error": {
            "type": "object",
            "properties": {
              "status": {
                "type": "string",
                "example": "error"
              },
              "message": {
                "type": "string",
                "example": "오류가 발생했습니다"
              },
              "code": {
                "type": "string",
                "example": "VALIDATION_ERROR"
              }
            }
          }
        }
      }
    }
  }
